from collections import deque
class Solution(object):
    def updateMatrix(self, mat):
        """
        :type mat: List[List[int]]
        :rtype: List[List[int]]
        """
        rows = len(mat)
        cols = len(mat[0])
        row,col = 0,0
        out = []
        for row in range(rows):
            out_row = []
            for col in range(cols):
                out_row.append(self.get_dist(mat,row,col))
            out.append(out_row)       
        return out

    def get_dist(self,mat,row,col):
        
        #if mat[row][col] == 0:
        #    return 0
        rows = len(mat)
        cols = len(mat[0])

        directions = [(1,0),(-1,0),(0,1),(0,-1)]
        distance = 0
        visited = set()
        stack = deque()
        stack.append([row,col,distance])
        while stack:
            i,j,distance = stack.popleft()

            if (i,j) in visited:
                continue
            
            visited.add((i,j))
            if mat[i][j] == 0:
                return distance
            else:
                distance += 1
            for direction in directions:
                next_i,next_j = i + direction[0],j + direction[1]
                if (0 <= next_i < rows) and (0 <= next_j < cols):
                    stack.append([next_i,next_j,distance])


