from collections import deque
mat = [[0,0,0],[0,1,0],[0,0,0]]
mat = [[0,0,0],[0,1,0],[1,1,1]]
mat = [[1,1,1],[1,1,1],[1,1,1]]

rows = len(mat)
cols = len(mat[0])
directions = [(1,0),(-1,0),(0,1),(0,-1)]
row,col = 0,0

def get_dist(row,col):
    
    #If not zero
    distance = 0
    visited = set()
    stack = deque()
    stack.append([row,col,distance])
    while stack:
        i,j,distance = stack.popleft()
        print(row,col,'iter',i,j,distance)
        #if visited 
        if mat[i][j] == 0:
            return distance
        else:
            distance += 1
        for direction in directions:
            next_i,next_j = i + direction[0],j + direction[1]
            if (0 <= next_i < rows) and (0 <= next_j < cols):
                stack.append([next_i,next_j,distance])

for row in range(rows):
    for col in range(cols):
        x = get_dist(row,col)
        print('res', row,col,x)


#%%
#solucao leetcode
class Solution(object):
    def updateMatrix(self, mat):

        rows = len(mat)
        cols = len(mat[0])

        q = []

        for i in range(rows):
            for j in range(cols):
                if mat[i][j] == 0:
                    q.append((i,j))
                else:
                    mat[i][j] = '#'
        for i,j in q:
            for di, dj in (1,0), (-1,0), (0,1), (0,-1):
                new_i, new_j = i + di, j + dj
                if (0 <= new_i < rows) and (0 <= new_j < cols) and (mat[new_i][new_j] == '#'):
                    mat[new_i][new_j] = mat[i][j] + 1
                    q.append((new_i,new_j))
        return mat
